double pow(double a, int n)
{
	if(n == 0) return 1.0;
	if(n % 2) return a * pow(a, n-1);
	else{
		double t = pow(a, n/2);
		return t * t;
	}
}

double comb(double n, double k)
{
	double ret = 1.0;
	for(int i = 0; i < k; i++){
		ret *= (n-i)/(k-i);
	}
	return ret;
}

