using namespace std;

const int N = 100;
const int M = 51;
const double EPS = 1e-9;

int n, m, L, t[N], v[N];
double p[N], C[M+1][M+1], ps[N][N], s_ps[N][N], ts[N][N];

main(){
  for(int i=0;i<=M;i++) C[i][0] = 1.0;
  for(int i=1;i<=M;i++) for(int j=1;j<=i;j++) C[i][j] = C[i-1][j-1] + C[i-1][j];
  cin >> n >> m >> L;
  for(int i=0;i<n;i++) cin >> p[i] >> t[i] >> v[i];
  bool f = true;
  for(int i=0;i<n;i++) if(v[i]) f = false;
  if(f){
    for(int i=0;i<n;i++) printf("%.8f\n", 0+EPS);
    return 0;
  }
  for(int i=0;i<n;i++){
    for(int j=0;j<=m;j++){
      ps[i][j] = C[m][j] * pow(p[i]/100.0, j) * pow((100 - p[i])/100.0, m - j);
      if(j == 0) s_ps[i][j] = ps[i][j];
      else s_ps[i][j] = s_ps[i][j-1] + ps[i][j];
      ts[i][j] = (double)L / v[i] + t[i] * j;
    }
  }
  for(int i=0;i<n;i++){
    double ans = 0.0;
    for(int j=0;j<=m;j++){
      double tmp = 1.0;
      for(int k=0;k<n;k++){
        if(k == i) continue;
        int l = lower_bound(ts[k], ts[k]+m+1, ts[i][j]+EPS) - ts[k];
        if(l != 0) tmp *= (1.0 - s_ps[k][l-1]);
      }
      ans += ps[i][j] * tmp;
    }
    printf("%.8f\n", ans+EPS);
  }
}

#include <iostream>
#include <cstdio>
#include <vector>
#include <cmath>
#include <algorithm>
#include <cassert>

using namespace std;

typedef long double real;
#define EQ(x,y) (abs((x)-(y))<EPS)

real EPS = 1e-9;

const int MAX_N = 101;
const int MAX_M = 53;

const bool debug = false;

int N, M; real L;
vector<real> P, T, V;
void input() {
    cin >> N >> M >> L;
    P.clear(); P.resize(N);
    T.clear(); T.resize(N);
    V.clear(); V.resize(N);
    for (int i = 0; i < N; i++) {
        cin >> P[i] >> T[i] >> V[i];
        P[i] /= 100;
    }
}

real X[MAX_N][MAX_M]; // X[i][j] :: im
real S[MAX_N][MAX_M]; // S[i][j] :: im

void init() {
    for (int i = 0; i < N; i++) for (int j = 0; j <= M; j++) X[i][j] = 0;
    for (int i = 0; i < N; i++) for (int j = 0; j <= M + 1; j++) S[i][j] = 0;
    for (int i = 0; i < N; i++) {
        static real dp[MAX_M][MAX_M];
        for (int j = 0; j <= M; j++) for (int k = 0; k <= M; k++) dp[j][k] = 0;
        dp[0][0] = 1;
        for (int j = 0; j < M; j++) {
            for (int k = 0; k <= j; k++) {
                dp[j + 1][k] += dp[j][k] * (1.0 - P[i]);
                dp[j + 1][k + 1] += dp[j][k] * P[i];
            }
        }
        for (int k = 0; k <= M; k++) {
            X[i][k] = dp[M][k];
        }
    }
    for (int i = 0; i < N; i++) {
        S[i][0] = 0;
        for (int j = 0; j <= M; j++) {
            S[i][j + 1] = S[i][j] + X[i][j];
        }
    }
}

void solve() {
    init();
    for (int i = 0; i < N; i++) { // i
        if (V[i] == 0) {
            printf("%.12Lf\n", real(0));
            continue;
        }
        real ans = 0;
        for (int j = 0; j <= M; j++) { // j
            real p = X[i][j];
            real time = L / V[i] + j * T[i];
            for (int k = 0; k < N; k++) { // k
                if (i == k) continue;
                if (EQ(T[k], 0)) {
                    p *= (L / V[k] - EPS > time);
                } else {
                    // c
                    int c = max(0, int(ceil((time - L / V[k]) / T[k] + EPS) + EPS));
                    if (c > M) {
                        p = 0;
