ll combination(ll n, ll r) {
  static ll comb[51][51];
  ll &ret = comb[n][r];
  if(ret != 0) return ret;
  if(n == 0 || r == 0) return ret = 1;
  if(n == r) return ret = 1;
  if(r == 1) return ret = n;
  return ret = combination(n - 1, r - 1) + combination(n - 1, r);
}

bool ok(int ai, int aj, int bi, int bj) {
  double ta = V[ai] > 0 ? (double) L / V[ai] + T[ai] * aj : INF;
  double tb = V[bi] > 0 ? (double) L / V[bi] + T[bi] * bj : INF;
  return ta < tb;
}

