int Find(double x) {
	int l = 1, r = tot;
	while (l <= r) {
		int mid = (l + r) >> 1;
		if (fabs(c[mid] - x) < eps)
			return mid;
		if (c[mid] > x)
			r = mid - 1;
		else
			l = mid + 1;
	}
	if (fabs(c[1] - x) < eps)
		return 1;
	if (fabs(c[tot] - x) < eps)
		return tot;
	return 0;
}

double co[MAXN][MAXM];

inline int lowbit(int x) {
	return x & (-x);
}

inline void Add(int i, int x, double v) {
	if (x == 0)
		return;
	for (; x <= tot; x += lowbit(x))
		co[i][x] += v;
}

inline double Query(int i, int x) {
	double ret = 0;
	for (; x > 0; x -= lowbit(x))
		ret += co[i][x];
	return ret;
}

double Pow(double a, int b) {
	double ret = 1.0;
	while (b) {
		if (b & 1)
			ret = ret * a;
		b >>= 1;
		a = a * a;
	}
	return ret;
}

