			b[++tot] = -(org[i] + j * t[i]);
		}
	sort(b + 1, b + 1 + tot);
	int tmp = 0;
	for (int i = 1; i <= tot; ++i) {
		if (i == 1 || fabs(b[i] - b[i - 1]) > eps) {
			c[++tmp] = b[i];
		}
	}
	tot = tmp;
	for (int i = 1; i <= n; ++i) {
		for (int j = 0; j <= m; ++j) {
