#include <bits/stdc++.h>
using namespace std;

#define for_(i,a,b) for(int i=(a);i<(b);++i)
#define for_rev(i,a,b) for(int i=(a);i>=(b);--i)
#define allof(a) (a).begin(),(a).end()
#define size_of(a) (int)(a).size()
#define minit(a,b) memset(a,b,sizeof(a))

int N, M, L, P[110], T[110], V[110];
double RP[110][55], RT[110][55];

int main() {
	cin >> N >> M >> L;
	for_(i,0,N) cin >> P[i] >> T[i] >> V[i];

	for_(i,0,N) {
		RT[i][0] = V[i] > 0 ? (double)L / (double)V[i] : 1e9;
		for_(j,0,M) RT[i][j + 1] = RT[i][j] + (double)T[i];
	}

	for_(i,0,N) {
		vector< double > p(M+1, 1.0), pm(M+1, 1.0);

		for_(k,0,M) {
			p[k + 1] = p[k] * (double)P[i] / 100.0;
			pm[k + 1] = pm[k] * (1.0 - (double)P[i] / 100.0);
		}

		int mCk = 1;

		for_(k,0,M + 1) {
			RP[i][k] =  (double)mCk * p[k] * pm[M - k];
			mCk *= (M - k);
			mCk /= k + 1;
		}

		for_rev(k,M,1) RP[i][k-1] += RP[i][k];
	}

	for_(i,0,N) {
		double ans = 0.0;

		for_(k,0,M+1) {
			double t = RT[i][k];
			double p = RP[i][k] - RP[i][k + 1];

			for_(ii,0,N) {
				if (ii == i) continue;
				int id = upper_bound(RT[ii], RT[ii] + M + 1, t) - RT[ii];

				if (id > M) {
					p = 0.0;
					break;
				} else {
					p *= RP[ii][id];
				}
			}

			ans += p;
		}

		cout << setprecision(9) << setiosflags(ios::fixed) << ans << endl;
	}
}

#include <bits/stdc++.h>
using namespace std;

#define for_(i,a,b) for(int i=a;i<b;++i)
#define for_rev(i,a,b) for(int i=a;i>=b;--i)

int N, M, L, P[110], T[110], V[110];
double p[110][55];

void solve() {
	for_(i,0,N) {
		double dp[55][55] = { 0.0 }, pi = (double)P[i] / 100.;
		dp[0][0] = 1.0;

		for_(j,0,M) for_(k,0,M) {
			dp[j+1][k] += dp[j][k] * (1.0 - pi);
			dp[j+1][k+1] += dp[j][k] * pi;
		}

		for_(k,0,M+1) p[i][k] = dp[M][k];
	}

	for_(i,0,N) {
		double ans = 0.0;

		if (V[i] > 0) {
			for_(j,0,M+1) {
				double t1 = (double)L / (double)V[i] + (double)(j * T[i]);
				double mul = 1.0;

				for_(ii,0,N) {
					if (i != ii && V[ii] > 0) {
						double mii = 0.0;								
						double t2 = (double)L / (double)V[ii] + (double)(M * T[ii]);

						for_rev(k,M,0) {
							if (t2 - t1 < 1e-9) break;
							mii += p[ii][k];
							t2 -= (double)T[ii];
						}

						mul *= mii;
					}
				}

				ans += mul * p[i][j];
			}
		}

		printf("%.9lf\n", (N == 1) ? 1.0 : ans);
	}
}

int main() {
	cin >> N >> M >> L;
	for_(i,0,N) cin >> P[i] >> T[i] >> V[i];

	solve();

	return 0;
}

