int main()
{
    while (scanf("%d%d%d",&N,&M,&L) != EOF)
    {
        REP(i,N) scanf("%d%d%d",&p[i],&t[i],&v[i]);
        REP(i,N) pp[i] = p[i] / 100.0;
        REP(i,N)
        {
            REP(j,M+1) REP(k,M+1) tmp[j][k] = 0;
            tmp[0][0] = 1.00;
            for (int j = 1;j <= M;++j)
                for (int k = 0;k <= j;++k)
                {
                    tmp[j][k] = tmp[j-1][k] * (1-pp[i]);
                    if (k) tmp[j][k] += tmp[j-1][k-1] * pp[i];
                }
            for (int j = M;j >= 0;--j)
            {
                rest[i][j] = tmp[M][j];
                rsum[i][j] = tmp[M][j];
           //     cout<<rest[i][j]<<" ";
                if (j!=M) rsum[i][j] += rsum[i][j+1];
            }
        }
        REP(i,N)
        {
            double ans = 0;
            for (int times = 0;times <= M && v[i] != 0;++times)
            {
                double part = 1.00;
                double use = L / (double)v[i] + t[i] * times;
                for (int k = 0;k < N;++k)
                    if (k != i)
                    {
                        if (v[k] == 0) continue;
                        int l = 0,r = M;
                        while (l < r)
                        {
                            int mid = (l+r)>>1;
                            double total = L / (double) v[k] + t[k] * mid;
                            if (total <= use)
                                l = mid+1;
                            else r = mid;
                        }
                        double total = L / (double) v[k] + t[k] * l;

                      //  --l;
                        if (total <= use)
                        {
                            part = 0.00;
                            break;
                        } else
                            part *= rsum[k][l];
                    }
                ans += rest[i][times] * part;
            }
            printf("%.8f\n",ans);
        }
    }
    return 0;
}

