double Pow(double x, int y){  
    double ans = 1.0;  
    while(y){  
        if(y&1)ans *= x;  
        x = x*x;  
        y >>= 1;  
    }  
    return ans;  
}  
double work(int x){  
    double ans = 0.0;  
    for(int i = 0; i <= m; i++)  
    {  
        double pp = C[m][i] * Pow(p[x], i) * Pow(1.0-p[x], m-i);  
        for(int j = 1; j <= n; j++)  
        {  
            if(j == x)continue;  
            double siz = 0;  
            for(int k = m; k >= 0; k--)  
            {  
                if(l*v[j] + i*t[x]*v[j]*v[x] < l*v[x] + k*t[j]*v[x]*v[j])  
                    siz += C[m][k] * Pow(p[j], k) * Pow(1.0-p[j], m-k);  
                else break;  
            }  
            pp *= siz;  
        }  
        ans += pp;  
    }  
    return ans;  
}  
