LL comb(LL n,LL m){
	LL ret=1,i;
	m=m<(n-m)?m:(n-m);
	for (i=n-m+1;i<=n;ret*=(i++));
	for (i=1;i<=m;ret/=(i++));
	return m<0?0:ret;
}

