using namespace std;

const int N = 100;
const int M = 51;
const double EPS = 1e-9;

int n, m, L, t[N], v[N];
long double p[N], C[M+1][M+1], ps[N][N], s_ps[N][N], ts[N][N];

main(){
  for(int i=0;i<=M;i++) C[i][0] = 1.0;
  for(int i=1;i<=M;i++) for(int j=1;j<=i;j++) C[i][j] = C[i-1][j-1] + C[i-1][j];
  cin >> n >> m >> L;
  for(int i=0;i<n;i++) cin >> p[i] >> t[i] >> v[i];
  bool f = true;
  for(int i=0;i<n;i++) if(v[i]) f = false;
  if(f){
    printf("%.8f\n", 0+EPS);
    return 0;
  }
  for(int i=0;i<n;i++){
    for(int j=0;j<=m;j++){
      ps[i][j] = C[m][j] * pow(p[i]/100.0, j) * pow((100 - p[i])/100.0, m - j);
      if(j == 0) s_ps[i][j] = ps[i][j];
      else s_ps[i][j] = s_ps[i][j-1] + ps[i][j];
      ts[i][j] = (double)L / v[i] + t[i] * j;
    }
  }
  for(int i=0;i<n;i++){
    double ans = 0.0;
    for(int j=0;j<=m;j++){
      double tmp = 1.0;
      for(int k=0;k<n;k++){
        if(k == i) continue;
        int l = lower_bound(ts[k], ts[k]+m+1, ts[i][j]+EPS) - ts[k];
        if(l != 0) tmp *= (1.0 - s_ps[k][l-1]);
      }
      ans += ps[i][j] * tmp;
    }
    printf("%.8f\n", ans+EPS);
  }
}

#include <bits/stdc++.h>
using namespace std;

#define for_(i,a,b) for(int i=(a);i<(b);++i)
#define for_rev(i,a,b) for(int i=(a);i>=(b);--i)
#define allof(a) (a).begin(),(a).end()
#define size_of(a) (int)(a).size()
#define minit(a,b) memset(a,b,sizeof(a))

int N, M, L, P[110], T[110], V[110];
double RP[110][55], RT[110][55];

int main() {
	cin >> N >> M >> L;
	for_(i,0,N) cin >> P[i] >> T[i] >> V[i];

	minit(RP, 0);

	for_(i,0,N) {
		RT[i][0] = V[i] > 0 ? (double)L / (double)V[i] : 1e9;
		for_(j,0,M) RT[i][j + 1] = RT[i][j] + (double)T[i];
	}

	for_(i,0,N) {
		vector< double > p(M+1, 1.0), pm(M+1, 1.0);

		for_(k,0,M) {
			p[k + 1] = p[k] * (double)P[i] / 100.0;
			pm[k + 1] = pm[k] * (1.0 - (double)P[i] / 100.0);
		}

		int mCk = 1;

		for_(k,0,M + 1) {
			RP[i][k] =  (double)mCk * p[k] * pm[M - k];
			mCk *= (M - k);
			mCk /= k + 1;
		}

		for_rev(k,M,1) RP[i][k-1] += RP[i][k];
	}

	for_(i,0,N) {
		if (V[i] == 0) {
			cout << "0.000000000" << endl;
			continue;
		}

		double ans = 0.0;

		for_(k,0,M+1) {
			double t = RT[i][k];
			double p = RP[i][k] - RP[i][k + 1];

			for_(ii,0,N) {
				if (ii == i || V[ii] == 0) continue;
				int id = upper_bound(RT[ii], RT[ii] + M + 1, t) - RT[ii];
				p *= RP[ii][id];
			}

			ans += p;
		}

		cout << setprecision(9) << setiosflags(ios::fixed) << ans << endl;
	}
}

