typedef long long LL;
const int maxn = 100000;
const double eps = 1e-7;
int n,m,s;
long long C[55][55];
struct node{
	int p,t,v;
}a[110];
void init(){
	C[0][0]=1;C[1][0]=1;
	for(int i=1;i<=51;i++)C[i][0]=1;
	for(int i=1;i<=51;i++)
		for(int j=1;j<=i;j++)
			C[i][j]=C[i-1][j]+C[i-1][j-1];
}
double calc(int idx,int x){
	if(x>m)return 0;
	double ans=0;
	for(int i=x;i<=m;i++){
		ans+=C[m][i]*pow(a[idx].p*1.0/100,i)*pow(1-a[idx].p*1.0/100,m-i);
	}
	return ans;
}
int main(int argc, char* argv[]){
	init();
	scanf("%d%d%d",&n,&m,&s);
	for(int i=1;i<=n;i++)
		scanf("%d%d%d",&a[i].p,&a[i].t,&a[i].v);
	for(int i=1;i<=n;i++){
		double ans=0;
		for(int j=0;j<=m;j++){
			double t=s*1.0/a[i].v+a[i].t*j;
			double p1=1;
			for(int k=1;k<=n;k++){
				if(i==k)continue;
				if(a[k].t==0){
					double tk1=s*1.0;
					if(tk1>t){p1*=calc(k,0);}
					else p1=0;
					continue;
				}
				double tk1=s*1.0/a[k].v;
				int x=(t-tk1)/a[k].t;
				if(tk1+a[k].t*x<t||(tk1+a[k].t*x-t)<eps)x++;
				p1*=calc(k,x);
			}
			ans+=C[m][j]*pow(a[i].p*1.0/100,j)*pow(1-a[i].p*1.0/100,m-j)*p1;
		}
		printf("%.7f\n",ans);
	}

    return 0;
}

