int N, M, L;
int P[200], T[200], V[200];

double prob[200][200];
double sum[200][200];

void calc_Btry() {
    for (int i = 0; i < N; ++i) {
        prob[i][0] = 1.0;
        for (int j = 1; j <= M; ++j) prob[i][j] = 0.0;
        for (int j = 0; j < M; ++j) {
            for (int k = j; k >= 0; --k) {
                prob[i][k+1] += prob[i][k] * (double)(P[i]) / 100;
                prob[i][k] *= 1.0 - (double)(P[i]) / 100;
            }
        }
        for (int j = 0; j <= M; ++j) sum[i][j] = 0.0;
        sum[i][M] = prob[i][M];
        for (int j = M-1; j >= 0; --j) sum[i][j] = sum[i][j+1] + prob[i][j];
    }
}



int main() {
    while (cin >> N >> M >> L) {
        for (int i = 0; i < N; ++i) cin >> P[i] >> T[i] >> V[i];
        calc_Btry();

//        for (int i = 0; i < N; ++i) {
//            cout << i << " : ";
//            for (int j = 0; j <= M; ++j) cout << sum[i][j] << ", " ;
//            cout << endl;
//        }

        cout << fixed << setprecision(7);
        for (int i = 0; i < N; ++i) {
            double res = 0.0;
            for (int j = 0; j <= M; ++j) {
                double tmp = prob[i][j];
                double aite = 1.0;
                for (int k = 0; k < N; ++k) {
                    if (k == i) continue;

                    int lim = M;
                    while ( (double)(L)/V[k] + T[k]*lim > (double)(L)/V[i] + T[i]*j ) {
                        if (lim < 0) break;
                        --lim;
                    }
                    ++lim;

                    if (lim > M) aite = 0.0;
                    else aite *= sum[k][lim];
                }
                res += tmp * aite;
            }
            cout << res << endl;
        }
    }

    return 0;
}

