long double fact(int x){
  long double r=1;
  while(x){
    r*=x--;
  }
  return r;
}

int main(){
  int n,m,l;
  cin>>n>>m>>l;
  double ap[100][51],acp[100][52]={{}};
  double at[100][51];
  for(int i=0;i<n;i++){
    double p,t,v;
    cin>>p>>t>>v;
    for(int j=0;j<=m;j++){
      at[i][j]=l/v+t*j;
      ap[i][j]=pow(p/100,j)*pow(1-p/100,m-j)*fact(m)/fact(j)/fact(m-j);
      acp[i][j+1]=acp[i][j]+ap[i][j];
    }
  }
  for(int i=0;i<n;i++){
    double a=0;
    for(int j=0;j<=m;j++){
      double c=ap[i][j];
      for(int k=0;k<n;k++){
	if(i!=k){
	  int x=lower_bound(at[k],at[k]+m+1,at[i][j]+1e-9)-at[k];
	  c*=1-acp[k][x];
	}
      }
      a+=c;
    }
    cout<<fixed<<a<<endl;	  
  }
  return 0;
}

