#define zero(a) memset(a, 0, sizeof a)
#define minus(a) memset(a, -1, sizeof a)
#define watch(a) { cout << #a << " = " << a << endl; }
template<class T1, class T2> inline bool minimize(T1 &a, T2 b) { return b < a && (a = b, 1); }
template<class T1, class T2> inline bool maximize(T1 &a, T2 b) { return a < b && (a = b, 1); }

typedef long long ll;
int const inf = 1<<29;

int N, M, L;

vector<tuple<int, int, int>> vs;

double P[111][111];
double T[111][111];

namespace math { namespace combination {

#define COMBINATION_MAX 200
using return_type = ll;

namespace memorized { namespace combination { enum { max_value = COMBINATION_MAX }; bool __computed = false; inline bool computed() {if(__computed) { return true; } else { __computed = true; return false; }} return_type dp[max_value][max_value];} }

return_type comb(int N, int K) {
  using memorized::combination::dp;
  if(memorized::combination::computed()) { return dp[N][K]; }

  rep(i, memorized::combination::max_value) {
    dp[i][0] = 1;
    REP(j, 1, i+1) {
      dp[i][j] = dp[i-1][j-1] + dp[i-1][j];
//      dp[i][j] %= MOD;
    }
  }
  return dp[N][K];
}}}

void solve() {
  rep(i, N) {
    rep(j, M+1) {
      P[i][j] = pow(get<0>(vs[i]) * 0.01, j) * pow((100-get<0>(vs[i])) * 0.01, M - j) * math::combination::comb(M, j);
      if(j) T[i][j] = T[i][j-1] + get<2>(vs[i]);
      else T[i][0] = 1.0 * L / get<1>(vs[i]);
    }
  }

  rep(i, N) {
    double res = 0;

    if(get<1>(vs[i]) == 0) {
      cout << "0\n";
      continue;
    }

    rep(j, M+1) {
      double losers_prob = 1.0;
      rep(k, N) {
        if(i == k) continue;
        double sum = 0.0;
        rep(l, M+1) {
          if(T[i][j] < T[k][l]) sum += P[k][l];
        }
        losers_prob *= sum;
      }
      res += losers_prob * P[i][j];
    }
    printf("%.10f\n", res);
  }
}

int main() {

  cin >> N >> M >> L;
  rep(i, N) {
    int p, t, v; cin >> p >> t >> v;
    vs.push_back({p, v, t});
  }

  N = vs.size();
  solve();

  return 0;
}

