double RepeatedPow(double base, int64_t exponent) {
  if (exponent == 0)
    return 1;
  else if (exponent % 2 == 0) {
    double root = RepeatedPow(base, exponent / 2);
    return (root * root);
  } else {
    return (base * RepeatedPow(base, exponent - 1));
  }
}

int64_t ncr[101][101]={};

double probability(int m,int p, int a){
  double result=1;
  result*=ncr[m][a];
  result*=pow(p/(double)100,a);
  result*=pow((1-p/(double)100),m-a);
  return result;
}

int main(){
  cout << std::fixed << std::setprecision(16);
  cin.tie(nullptr);
  std::ios::sync_with_stdio(false);

  int n,m,l;
  cin>>n>>m>>l;



  for(int i=0;i<=100;i++){
    for(int j=0;j<=i;j++){
      if(j==0||j==i){
        ncr[i][j]=1;
      }else{
        ncr[i][j]=ncr[i-1][j-1]+ncr[i-1][j];
      }
    }
  }

  std::vector<Runner> runner(n);
  for(int i=0;i<n;i++){
    int p, t, v;
    cin >> p >> t >> v;
    runner[i]=Runner(p,t,v);
  }

  for(int i=0;i<n;i++){
    double result=0;
    for(int a=0;a<=m;a++){
      double term=probability(m,runner[i].p,a);
      double i_time=(double)l/runner[i].v+a*runner[i].t;
      for(int b=0;b<n;b++){
        if(i==b) continue;
        double win_rate=0;
        for(int c=m;c>=0;c--){
          double probability_b=probability(m,runner[b].p,c);
          double b_time=(double)l/runner[b].v+c*runner[b].t;
          if(i_time<b_time){
            win_rate+=probability_b;
          }else{
            break;
          }
        }
        term*=win_rate;
      }
      result+=term;
    }
    cout<<result<<endl;
  }

  return 0;
}

