#include <stdio.h>
#include <cstring>

using namespace std;

const int MAXN = 110;
const double eps = 1e-12;

double pro[MAXN][55][55];
double speed[MAXN], rest[MAXN], p[MAXN];
double to[MAXN][55];
double dis;
int n, m;

void read()
{
    for(int i = 0; i < n; ++i)
    {
        scanf("%lf%lf%lf", p + i, rest + i, speed + i);
        p[i] *= 0.01;
    }

    memset(pro, 0, sizeof(pro));
    for(int i = 0; i < n; ++i)
    {
        pro[i][0][0] = 1.0;
        for(int j = 1; j <= m; ++j)
        {
            pro[i][j][0] = pro[i][j - 1][0] * (1 - p[i]);
            for(int k = 1; k <= j; ++k)
                pro[i][j][k] = pro[i][j -  1][k] * (1 - p[i]) + pro[i][j - 1][k - 1] * p[i];
        }
    }

    for(int i = 0; i < n; ++i)
    {
        for(int j = 0; j <= m; ++j)
            to[i][j] = dis / speed[i] + j * rest[i];
    }
}
double ans[MAXN];
int main()
{
    while(scanf("%d%d%lf", &n, &m, &dis) != EOF)
    {
        read();
        for(int i = 0; i < n; ++i)
        {
            ans[i] = 0.0;
            for(int j = 0; j <= m; ++j)
            {
                double hh(pro[i][m][j]);
                for(int k = 0; k < n; ++k)
                {
                    if(k == i)  continue;
                    double tmp = 1.0;
                    for(int u = 0; u <= m; ++u)
                    {
                        if(to[k][u] > to[i][j] + eps)  break;
                        tmp -= pro[k][m][u];
                    }
                    hh *= tmp;
                }
                ans[i] += hh;
            }
        }
        for(int i = 0; i < n; ++i)
            printf("%lf\n", ans[i]);
    }


    return 0;
}

#include<bits/stdc++.h>
using namespace std;
#define eps (1e-8)

int N,M;
double L;
double P[100],T[100],V[100];
double a[100][100],b[100][100];

double mem[100][100];
double ncr(int n,int r){
  if(mem[n][r]!=-1.0)return mem[n][r];
  r=min(r,n-r);
  double res=1.0;
  for(int i=0;i<r;i++){
    res=res*(double)(n-i);
    res=res/(double)(i+1);
  }
  return mem[n][r]=res;
}

double solve(int x){
  if(V[x]<eps)return 0;

  double res=0;
  for(int i=0;i<=M;i++){
    double p=a[x][i];
    double d=b[x][i];
    for(int j=0;j<N;j++){
      if(x==j)continue;
      if(V[j]<eps){
        continue;
      }
      double sum=0;
      for(int k=0;k<=M;k++){
        if(b[j][k]<d+eps)continue;
        sum+=a[j][k];
      }
      p*=sum;
    }
    res+=p;
  }
  return res;
}

int main(){
  for(int i=0;i<100;i++)
    for(int j=0;j<100;j++)
      mem[i][j]=-1.0;

  cin>>N>>M>>L;
  for(int i=0;i<N;i++){
    cin>>P[i]>>T[i]>>V[i];
    if(V[i]<eps)continue;

    double p=P[i]/100.0;
    double q=1.0-p;
    for(int j=0;j<=M;j++){
      a[i][j]=ncr(M,j)*pow(p,j)*pow(q,M-j);
      b[i][j]=L/V[i]+T[i]*j;
      //cout<<a[i][j]<<' '<<b[i][j]<<endl;
    }
    //cout<<endl;
  }
  for(int i=0;i<N;i++){
    printf("%.10f\n",solve(i));
  }
  return 0;
}

