    for(int i=0;i<n;i++){
        cin>>p[i]>>t[i]>>v[i];
        p[i]/=100;
    }

    vector<vector<ld>> pos(n);
    for(int i=0;i<n;i++){
        vector<vector<ld>> comb(m+1,vector<ld>(m+1,0));
        comb[0][0]=1;
        for(int j=0;j<=m;j++){
            for(int k=0;k<=j;k++){
                if(k==0){
                    comb[j][k]=(p[i]==1 ? 0 : pow(1-p[i],j));
                }
                else if(k==j){
                    comb[j][k]=(p[i]==0 ? 0 : pow(p[i],j));
                }
                else{
                    comb[j][k]=comb[j-1][k-1]*p[i]+comb[j-1][k]*(1-p[i]);
                }
            }
        }
        pos[i]=comb[m];
    }
    vector<vector<ld>> possum(n,vector<ld>(m+2));
    for(int i=0;i<n;i++){
        partial_sum(pos[i].begin(),pos[i].end(),possum[i].begin()+1);
    }

    vector<ld> res(n,0);
    for(int i=0;i<n;i++){
        if(v[i]==0) continue;
        for(int x=0;x<=m;x++){
            ld sc=pos[i][x];
            for(int j=0;j<n;j++){
                if(i==j || v[j]==0) continue;
                ld z=0;
                for(int y=0;y<=m;y++){
                    if(l*v[j]+x*t[i]*v[i]*v[j]<l*v[i]+y*t[j]*v[i]*v[j]){
                        z+=pos[j][y];
                    }
                }
                sc*=z;
            }
            res[i]+=sc;
        }
    }
    cout<<setprecision(8)<<fixed;
    for(int i=0;i<n;i++){
        cout<<res[i]<<endl;
    }

    return 0;
}

