/* monkukui  ()*/

double comb(int n, int r) {
  double ue = 1;
  double sita = 1;
  for(int i = 0; i < r; i++) ue *= (n - i);
  for(int i = r; i >= 1; i--) sita *= i;
  return ue / sita;
}

int main(){

  double l;
  int n, m; cin >> n >> m >> l;

  vector<double> p(n);
  vector<double> t(n);
  vector<double> v(n);

  for(int i = 0; i < n; i++) cin >> p[i] >> t[i] >> v[i];

  vector<vector<double>> g(n, vector<double> (m + 1, 0.0));

  for(int i = 0; i < n; i++) {
    for(int j = 0; j <= m; j++) {
      // i , j 
      double val = pow(p[i] / (double)100, j) * pow(1 - p[i] / (double)100, m - j) * comb(m, j);
      g[i][j] = val;

    }
  }

  for(int i = 0; i < n; i++) {
    // i 
    double ans = 0.0;

    for(int j = 0; j <= m; j++) {
      // i , j 
      // i  it 
      double mul = 1.0;
      double it = l / v[i] + t[i] * j;

      // 
      for(int k = 0; k < n; k++) {
        if(i == k) continue;
        double add = 0.0;
        for(int ll = 0;ll <= m; ll++) {
          // k  ll 
          // 
          double kt = l / v[k] + t[k] * ll;
          if(it < kt){
            add += g[k][ll];
          }
        }

        mul *= add;
      }

      ans += mul * g[i][j];
    }

    printf("%.10f\n", ans);
  }

  return 0;
}

