using namespace std;

template<class T> constexpr T INF = ::numeric_limits<T>::max() / 32 * 15 + 208;

int main() {
    int n, m, l;
    cin >> n >> m >> l;
    using P = pair<double, double>;
    vector<vector<P>> v(n, vector<P>(m+1, {0,0}));
    for (int i = 0; i < n; ++i) {
        double p, t, k;
        cin >> p >> t >> k;
        p /= 100; k = l/k;
        vector<vector<double>> dp(m+1, vector<double>(m+1, 0));
        dp[0][0] = 1.0;
        for (int j = 0; j < m; ++j) {
            for (int jj = 0; jj <= j; ++jj) {
                dp[j+1][jj] += dp[j][jj]*(1-p);
                dp[j+1][jj+1] += dp[j][jj]*p;
            }
        }
        for (int j = 0; j <= m; ++j) {
            v[i][m-j] = make_pair(-k-t*j, dp[m][j]);
        }
        for (int j = 0; j <= m; ++j) {
            if(j) v[i][j].second += v[i][j-1].second;
        }
    }
    for (int i = 0; i < n; ++i) {
        double ans = 0;
        for (int j = 0; j <= m; ++j) {
            double x = v[i][j].second;
            if(j) x -= v[i][j-1].second;
            for (int k = 0; k < n; ++k) {
                if(i == k) continue;
                auto cur = lower_bound(v[k].begin(),v[k].end(), make_pair(v[i][j].first, 0.0));
                if(cur == v[k].begin()) x *= 0;
                else {
                    cur--;
                    x *= cur->second;
                }
            }
            ans += x;
        }
        printf("%.10lf\n", ans);
    }
    return 0;
}

