    cin >> P[i] >> T[i] >> V[i];
  }
  double prob[N][M+1];

  for(int i = 0; i < N; ++i) prob[i][0] = 1.0;
  for(int i = 0; i < N; ++i){

    for(int j = 1; j <= M; ++j){
      prob[i][j] = prob[i][j-1]*P[i]/100.0*(M-j+1)/j;
    }
    double t = 1.0;
    for(int j = M; j >= 0; --j){
      prob[i][j] *= t;
      t *= (1.0 - (double)P[i]/100.0);
    }
  }
  double acum[N][M+2];
  for(int i = 0; i < N; ++i) acum[i][M+1] = 0;
  for(int i = 0; i < N; ++i){
    for(int j = M; j >= 0; --j){
      acum[i][j] = acum[i][j+1] + prob[i][j];
    }
  }
  vector< vector<double> > time(N,vector<double>(M+1));
  for(int i = 0; i < N; ++i) time[i][0] = (double)L/V[i];
  for(int i = 0; i < N; ++i){
    for(int j = 1; j <= M; ++j){
      time[i][j] = time[i][j-1] + T[i];
    }
  }
  for(int i = 0; i < N; ++i){
    double ans = 0.0;
    for(int j = M; j >= 0; --j){
      double t = prob[i][j];
      for(int k = 0; k < N; ++k){
        if(k == i) continue;
        int id = upper_bound(time[k].begin(),time[k].end(),time[i][j]) - time[k].begin();
        if(id > M){
          t = 0;
          break;
        }
        t *= acum[k][id];
      }
      ans += t;
    }
    printf("%.12f\n",ans);
  }
  return 0;
}

