LL n, m, l;

double ff (LL u, LL state, LL v) {
	double ret = 0;
	for (LL i = 0; i <= m; ++i) {
		if (lv[v] + i * t[v] > lv[u] + state * t[u]) {
			ret += 1.0 * c[i][m] * pow (1.0 * p[v], 1.0*i) * pow (1.0 - p[v], 1.0*m - i);
		}
	}
	return ret;
}
double f (LL u, LL state) {
	double ret = 1.0 * c[state][m] * pow (1.0 * p[u], 1.0 * state) * pow (1.0 - p[u], 1.0 * m - state);
	for (LL i = 0; i < n; ++i) {
		if (i == u) continue;
		ret *= ff (u, state, i);
	}
	return ret;
}
double solve (LL u) {
	double ret = 0;
	for (LL i = 0; i <= m; ++i) {
		ret += f (u, i);
	}
	return ret;
}

//&#32452; c(m, n)
LL combination(LL m, LL n) {
	LL i, a, b, p;
	if (n < m) n ^= m ^= n ^= m;
	p = 1;
	a = n - m < m ? n-m : m;
	b = n - m > m ? n-m : m;
	for (i = 1; i <= a; ++i) p += p * b / i;
	return p;
}

