double fast_pow(double a, int b) {
    double ret = 1;
    while(b) {
        if(b & 1) ret *= a;
        a = a * a;
        b /= 2;
    }
    return ret;
}

double cal(int a, double b) {
//    return 1;
    double ret = 1;
    for(int i = 0; i < n; i++) {
        if(i == a) continue;
        if(b > v[i] + m * t[i] - eps) {
            return 0;
        }
        int l = 0;
        int r = m;
        int mid;
        int ans;
        while(l <= r) {
            mid = (l + r) / 2;
            if(v[i] + mid * t[i] > b) {
                r = mid - 1;
                ans = mid;
            } else {
                l = mid + 1;
            }
        }
        ret *= num[i][ans];
    }
//    printf("%d %lf %lf\n", a, b, ret);
    return ret;
}

double solve(int a) {
    double ret = 0;
    for(int i = 0; i <= m; i++) {
        ret += fast_pow(p[a], i) * fast_pow(1 - p[a], m - i) * c[m][i] * cal(a, v[a] + t[a] * i);
    }
    return ret;
}

