struct Stat {
  double t, p;
  int i, j;
  Stat() {}
  Stat(double _t, double _p, int _i, int _j): t(_t), p(_p), i(_i), j(_j) {}
  bool operator<(const Stat &s) const { return t < s.t; }
};

typedef vector<Stat> vs;

/* global variables */

ll combs[MAX_M + 1][MAX_M + 1];
Stat rs[MAX_N][MAX_M + 1];
double psums[MAX_N][MAX_M + 2], ans[MAX_N];
