  int n, m, l;
  cin >> n >> m >> l;

  for (int i = 0; i <= m; i++) {
    combs[i][0] = combs[i][i] = 1;
    for (int j = 1; j < i; j++)
      combs[i][j] = combs[i - 1][j - 1] + combs[i - 1][j];
  }

  vs allrs;

  for (int i = 0; i < n; i++) {
    int pi, ti, vi;
    cin >> pi >> ti >> vi;

    double p = (double)pi / 100;
    double t0 = (double)l / vi;
    for (int j = 0; j <= m; j++) {
      double tj = t0 + ti * j;
      double pj = combs[m][j] * pow(p, j) * pow(1.0 - p, m - j);
      rs[i][j] = Stat(tj, pj, i, j);
      allrs.push_back(rs[i][j]);
    }
    psums[i][m + 1] = 0.0;
    for (int j = m; j >= 0; j--) psums[i][j] = psums[i][j + 1] + rs[i][j].p;
    //printf("%d: %lf\n", i, psums[i][0]);
  }

  sort(allrs.begin(), allrs.end());
  int rn = allrs.size();

  for (int i = 0; i < n; i++) ans[i] = 0.0, cs[i] = 0;

  for (int i = 0; i < rn; i++) {
    Stat &r = allrs[i];
    double pp = r.p;

    for (int j = 0; j < n; j++)
      if (r.i != j) {
	int cj = cs[j];
	if (cj <= m && rs[j][cj].t == r.t) cj++;
	pp *= psums[j][cj];
      }

    ans[r.i] += pp;
    cs[r.i]++;
  }

  for (int i = 0; i < n; i++) printf("%.8lf\n", ans[i]);
  return 0;
}

