int n, m, l, p[111], t[111], v[111]; double r[111][55], dp[55][55];
int main() {
	cin >> n >> m >> l;
	for (int i = 0; i < n; i++) cin >> p[i] >> t[i] >> v[i];
	for (int i = 0; i < n; i++) {
		for (int j = 0; j <= m; j++) fill(dp[j], dp[j] + m + 1, 0.0);
		dp[0][0] = 1;
		for (int j = 0; j < m; j++) {
			for (int k = 0; k <= j; k++) {
				dp[j + 1][k] += dp[j][k] * (100 - p[i]) / 100;
				dp[j + 1][k + 1] += dp[j][k] * p[i] / 100;
			}
		}
		for (int j = 0; j <= m; j++) r[i][j] = dp[m][j];
	}
	for (int i = 0; i < n; i++) {
		double ret = 0;
		if (v[i] != 0) {
			double time1 = l / v[i];
			for (int j = 0; j <= m; j++, time1 += t[i]) {
				double res = r[i][j];
				for (int k = 0; k < n; k++) {
					if (i == k) continue;
					double sum = 0;
					if (v[k] != 0) {
						double time2 = l / v[k];
						for (int l = 0; l <= m; l++, time2 += t[k]) {
							if (time1 < time2) sum += r[k][l];
						}
					}
					else res = 1;
					res *= sum;
				}
				ret += res;
			}
		}
		cout << fixed << setprecision(15) << ret << endl;
	}
	return 0;
}

