double nCr(int n, int r){
  if(r==0||n==r)return 1;
  double a=1,b=1;
  for(int i=0;i<n-r;i++)a*=(n-i);
  for(int i=1;i<=r;i++)b*=i;
  return a/b;
}

double DP(){
  double dp[N][M]={},a=1,b=1;
  for(int i=0;i<m;i++) a*=(1-p[0]);
  for(int i=0;i<=m;i++){
    dp[0][i]=a*b*nCr(m,i),b*=p[0],a/=(1-p[0]);
    if(a==0)break;
  }

  for(int i=1;i<n;i++){
    for(int j=0;j<=m;j++){
      a=b=1;
      for(int k=0;k<m;k++)a*=(1-p[i]);
      for(int k=0;k<=m;k++,b*=p[i],a/=(1-p[i])){
	if(l/v[0]+j*t[0]<l/v[i]+k*t[i]) dp[i][j]+= dp[i-1][j]*a*b*nCr(m,k);
	if(a==0)break;
      }

    }
  }
  double res=0;
  for(int i=0;i<=m;i++)res+=dp[n-1][i];
  return res;
}

