#define REP(i, a, n) for(int i = ((int) a); i < ((int) n); i++)
#define INF 1e10
using namespace std;
typedef long long ll;

int N, M, L, P[100], T[100], V[100];
double p[100][51];

ll combination(ll n, ll r) {
  static ll comb[51][51];
  ll &ret = comb[n][r];
  if(ret != 0) return ret;
  if(n == 0 || r == 0) return ret = 1;
  if(n == r) return ret = 1;
  if(r == 1) return ret = n;
  return ret = combination(n - 1, r - 1) + combination(n - 1, r);
}

bool ok(int ai, int aj, int bi, int bj) {
  double ta = V[ai] > 0 ? L / V[ai] + T[ai] * aj : INF;
  double tb = V[bi] > 0 ? L / V[bi] + T[bi] * bj : INF;
  return ta < tb;
}

int main(void) {
  cin >> N >> M >> L;
  REP(i, 0, N) cin >> P[i] >> T[i] >> V[i];

  REP(i, 0, N) REP(j, 0, M + 1) {
    p[i][j] = combination(M, j) * pow(P[i] / 100.0, j) * pow(1 - P[i] / 100.0, M - j);
  }

  REP(i, 0, N) {
    double ans = 0;
    double pi = 0;
    REP(j, 0, M + 1) {
      pi = p[i][j];
      double pij = pi;
      REP(k, 0, N) if(i != k) {
        double pk = 0;
        REP(l, 0, M + 1) if(ok(i, j, k, l)) pk += p[k][l];
        pij *= pk;
      }
      ans += pij;
    }
    printf("%.8lf\n", ans);
  }


  return 0;
}

