		double comb = 1.0;
		for(int j=m; j>=0; --j){
			time[i][j] = l / (double) v[i] + j * t[i];
			prob[i][j] = comb * pow(p[i]/100.0, j) * pow(1.0-p[i]/100.0, m-j);
			sumProb[i][j] = sumProb[i][j+1] + prob[i][j];
			comb *= j;
			comb /= m-j+1;
		}
	}

	for(int i=0; i<n; ++i){
		double ret = 0.0;
		for(int j=0; j<=m; ++j){
			double tmp = prob[i][j];
