				while(left < right){
					int middle = (left + right) / 2;
					if(time[i][j] < time[k][middle] - EPS){
						right = middle;
					}else{
						left = middle + 1;
					}
				}
				if(time[i][j] < time[k][left] - EPS)
					tmp *= sumProb[k][left];
