#define rep(i,a,n) for(int i=a; i<n; i++)
typedef long long int ll;
const int MAXN = 110;
const ll MAXM = 55;

ll comb[MAXM][MAXM];
double ac[MAXN][MAXM];

int main() {
  rep(i,0,MAXM) comb[i][0] = 1;
  rep(i,1,MAXM) rep(j,1,i+1) comb[i][j] = comb[i-1][j-1] + comb[i-1][j];

  int N, M, L;
  scanf("%d %d %d", &N, &M, &L);

  if (N == 0)
    return 1;

  std::vector<int> T(N), V(N);
  std::vector<double> P(N);
  for (int i=0; i<N; ++i) {
    int p;
    scanf("%d %d %d", &p, &T[i], &V[i]);
    P[i] = p / 100.0;
  }

  rep(i,0,N) rep(j,0,M+1) {
    double prev = (j ? ac[i][j-1] : 0);
    ac[i][j] = prev + comb[M][j] * pow(P[i], j) * pow(1-P[i], M-j);
  }

  rep(i,0,N) {
    double ans = 0;
    rep(j,0,M+1) {
      double cur = ac[i][j] - (j ? ac[i][j-1] : 0);
      double user_time = 1.0 * L / V[i] + T[i] * j;
      rep(x,0,N) {
        if(x == i) continue;
        bool flag = false;
        rep(y,0,M+1) {
          double rival_time = 1.0 * L / V[x] + T[x] * y;
          if(user_time < rival_time) {
            flag = true;
            double rival_prob = ac[x][M] - (y ? ac[x][y-1] : 0);
            cur *= rival_prob;
            // printf("user = (%d, %d), rival = (%d, %d), prob = %.12f\n", i+1, j, x+1, y, rival_prob);
            break;
          }
        }
        if(!flag) cur = 0;
      }
      ans += cur;
    }
    printf("%.12f\n", ans);
  }
  return 0;
}

