#define for_(i,a,b) for(int i=a;i<b;++i)
#define for_rev(i,a,b) for(int i=a;i>=b;--i)

int N, M;
double L, P[110], T[110], V[110];
double p[110][55];

void solve() {
	for_(i,0,N) {
		double dp[55][55] = { 0.0 }, pi = P[i] / 100.;
		dp[0][0] = 1.0;

		for_(j,0,M) for_(k,0,M) {
			dp[j+1][k] += dp[j][k] * (1.0 - pi);
			dp[j+1][k+1] += dp[j][k] * pi;
		}

		for_(k,0,M+1) p[i][k] = dp[M][k];
	}

	for_(i,0,N) {
		double ans = 0.0;

		if (V[i] > 0) {
			for_(j,0,M+1) {
				double t1 = L / V[i] + (j * T[i]);
				double mul = 1.0;

				for_(ii,0,N) {
					if (i != ii && V[ii] > 0) {
						double mii = 0.0;								
						double t2 = L / V[ii] + (M * T[ii]);

						for_rev(k,M,0) {
							if (t2 - t1 < 1e-9) break;
							mii += p[ii][k];
							t2 -= T[ii];
						}

						mul *= mii;
					}
				}

				ans += mul * p[i][j];
			}
		}

		printf("%.9lf\n", (N == 1) ? 1.0 : ans);
	}
}

int main() {
	cin >> N >> M >> L;
	for_(i,0,N) cin >> P[i] >> T[i] >> V[i];

	solve();

	return 0;
}

