#include <bits/stdc++.h>
using namespace std;

#define for_(i,a,b) for(int i=a;i<b;++i)

struct State {
	string dial; int cnt;
	State(string _d, int _c) : dial(_d), cnt(_c) {}
};

int k;
string S, T;

char add(char c, int x) {
	int y = c - '0';
	return char('0' + (y + x) % 10);
}

void solve() {
	set< string > vis;
	vis.insert(S);

	queue< State > q;
	q.push(State(S, 0));

	while (!q.empty()) {
		State s = q.front(); q.pop();

		if (s.dial == T) {
			cout << s.cnt << endl;
			break;
		}

		for_(i,0,k) if (s.dial[i] != T[i]) {
			int dif = (T[i] - s.dial[i] + 10) % 10;

			for_(j,i,k) {
				s.dial[j] = add(s.dial[j], dif);
				if (vis.find(s.dial) == vis.end()) q.push(State(s.dial, s.cnt + 1));
			}

			break;
		}
	}
}

int main() {
	while (cin >> k, k) {
		cin >> S >> T;
		solve();
	}

	return 0;
}

