#include <iostream>
#include <algorithm>
#include <cstdio>
#include <cmath>
#include <vector>
#include <map>
using namespace std;

typedef long long lli;

const double eps = 1e-6;
const int MAXN = 102;
const int MAXM = 52;
int N, M, L;
double P[MAXN];
int T[MAXN];
int V[MAXN];
lli comb[MAXM][MAXM];

vector<double> U[MAXN];
vector<double> Q[MAXN];
vector<double> R[MAXN];

bool equals(double a, double b) {
  return abs(a-b) < eps;
}

void make() {
  comb[0][0] = 1;
  for(int i = 1; i < MAXM; ++i) {
    for(int j = 0; j <= i; ++j) {
      comb[i][j] = 0;
      if(j) comb[i][j] += comb[i-1][j-1];
      if(j <= i-1) comb[i][j] += comb[i-1][j];
    }
  }
}

int main() {
  make();
  while(cin >> N >> M >> L) {
    for(int i = 0; i < N; ++i) {
      cin >> P[i] >> T[i] >> V[i];
      P[i] /= 100.0;
    }
    for(int i = 0; i < MAXN; ++i) {
      U[i].clear();
      Q[i].clear();
    }
    for(int i = 0; i < N; ++i) {
      for(int k = 0; k <= M; ++k) { // rest k times.
	U[i].push_back(L/(double)V[i]+(double)T[i]*k);
	Q[i].push_back(pow(P[i],k)*pow(1.0-P[i],M-k)*comb[M][k]);
      }
    }

    for(int i = 0; i < N; ++i) {
      double res = 0.0;
      for(int k = 0; k <= M; ++k) {
	double tmp = Q[i][k];
	for(int j = 0; j < N; ++j) {
	  if(i == j) continue;
	  /*
	  vector<double>::iterator it = 
	    lower_bound(U[j].begin(), U[j].end(), U[i][k]);
	  */
	  double a = 0;
	  for(int b = 0; b < U[j].size(); ++b) {
	    if(equals(U[j][b], U[i][k]) || U[j][b] < U[i][k]) continue;
	    a += Q[j][b];
	  }
	  tmp *= a;
	}
	res += tmp;
      }
      printf("%.8f\n", res);
    }
    //puts("");
  }
  return 0;
}

