double mypow(double b ,int e){
      if(e == 0) return 1;
      if(e%2==0)
            return mypow( b*b, e/2 );
      else
            return mypow( b*b, e/2 ) * b;
}

int main(){


      int N,M,L; cin >> N >> M >> L ;
      if(N==1){
            cout << 1.0 << endl;
            return 0;
      }
      vector<vd> prob(N,vd(M+1,0.0));
      vector<vd> dist(N,vd(M+1,0.0));

      vd p(N),t(N),v(N);
      for(int i=0; i<N; i++) cin >> p[i] >> t[i] >> v[i];

      // PRE PROCESS
      for(int i=0; i<N; i++){
            dist[i][0] = (double)L / v[i];
            for(int j=1; j<=M; j++){
                  dist[i][j] = dist[i][j-1] + t[i];
            }
      }
      for(int i=0; i<N; i++)
            prob[i][0] = mypow((100-p[i])/100,M);
      for(int i=0; i<N; i++){
            for(int j=1; j<=M; j++){
                  if(p[i] != 100)
                        prob[i][j] = (prob[i][j-1] * (p[i]/100)) / ((100-p[i])/100);
                  else
                        prob[i][j] = 1.0;
            }
      }
      double comb = 1;
      for(int j=1; j<=M; j++){
            comb = comb * (M - j + 1) / j; 
            for(int i=0; i<N; i++){
                  prob[i][j] *= comb;
            }
      }

      vector<vd> sum = prob;
      for(int i=0; i<N; i++){
            for(int j=M-1; j>=0; j--){
                  sum[i][j] += sum[i][j+1];
                  if(sum[i][j] > 1) sum[i][j] = 1;
            }
      }

      vector<vd> ans = prob;
      for(int i=0; i<N; i++){
            for(int j=0; j<=M; j++){
                  for(int k=0; k<N; k++){
                        if(i==k) continue;
                        int id = upper_bound(dist[k].begin(),dist[k].end(), dist[i][j]) - dist[k].begin();
                        if(id > M) ans[i][j] = 0.0;
                        else       ans[i][j] *= sum[k][id];

                  }
            }
      }

      for(int i=0; i<N; i++){
            double tmp = 0.0;
            if(p[i] == 100) tmp = ans[i][M];
            else
                  for(int j=0; j<=M; j++)
                        tmp += ans[i][j];
            if(tmp > 1) tmp = 0;
            printf("%.10f\n",tmp);
      }

}

#include<cstdio>
#include<cmath>
int n,m,l,p[100],t[100],v[100];
double time[100][51],poss[100][51],c[2][51],ans,sum[100][51];
int main(){
        scanf("%d%d%d",&n,&m,&l);
        c[1][0]=c[1][1]=1;
        for (int i=2;i<=m;i++){
                c[i%2][0]=c[i%2][i]=1;
		for (int j=1;j<i;j++) c[i%2][j]=c[(i+1)%2][j-1]+c[(i+1)%2][j];
        }
        for (int i=0;i<n;i++){
                scanf("%d%d%d",&p[i],&t[i],&v[i]);
                time[i][0]=(double)l/v[i];
                for (int j=1;j<=m;j++) time[i][j]=time[i][j-1]+t[i];
                for (int j=0;j<=m;j++) poss[i][j]=c[m%2][j]*pow((double)p[i]/100,j)*pow(1-(double)p[i]/100,m-j);
		sum[i][m]=poss[i][m];
		for (int j=m-1;j>=0;j--) sum[i][j]=sum[i][j+1]+poss[i][j];
        }
	for (int i=0;i<n;i++){
		ans=0;
		for (int j=0;j<=m;j++){
			double cur=1,cur_t=time[i][j];
			for (int k=0;k<n;k++) if (i!=k){
				int l=0,r=m,mid,res;
				while (l<=r){
					mid=l+r>>1;
					if (time[k][mid]>cur_t){
						res=mid;
						r=mid-1;
					}	
					else l=mid+1; 
				}
				if (time[k][res]>cur_t) cur*=sum[k][res];
				else cur=0;
			}
			ans+=cur*poss[i][j];
		}
		printf("%.10f\n",ans);
	}
	return 0;
}

