double ppow(double p,int k){
    if(k==0)return 1.0;
    else if(k==1)return p;
    double ret = ppow(p,k/2);
    if(k%2==0)return ret*ret;
    else return ret*ret*p;
}

double res[200];
double tmp[100];

int main(){
#ifndef ONLINE_JUDGE
    freopen("in","r",stdin);
#endif
    int i,j,k;
    double t;
    int ti;

