            pl[i].p=pl[i].p/100.0;
            for(j=0;j<=m;j++){
                rp[i][j]=c[m][j]*ppow((1.0-pl[i].p),m-j)*ppow(pl[i].p,j);
                if(j==0)sp[i][j]=rp[i][j];
                else sp[i][j]=sp[i][j-1]+rp[i][j];
            }
        }/*
        for(i=0;i<n;i++){
