        for(i=0;i<n;i++){
            res[i]=0;
            for(j=0;j<=m;j++){
                tmp[j]=1.0;
                for(k=0;k<n;k++){
                    if(i!=k){
                        if(l/pl[i].v+j*pl[i].t<l/pl[k].v){
                            continue;
                        }
                        else if(l/pl[i].v+j*pl[i].t>=l/pl[k].v+m*pl[k].t){
                            tmp[j]=0.0;
                            break;
                        }
                        else{
                            t=l/pl[i].v+j*pl[i].t-l/pl[k].v;
                            ti=(int)(t/pl[k].t);
                            //if(zero(pl[k].t*ti,t))ti++;
                            tmp[j]*=(1.0-sp[k][ti]);
                        }
                    }
                }
                res[i]+=tmp[j]*rp[i][j];
            }
        }
        for(i=0;i<n;i++){
            printf("%.5f\n",res[i]);
        }
        //printf("\n");
    }

    return 0;
}

