Int C[100][100];

int main() {
    C[0][0] = 1;
    rep(i, 80) rep(j, 80) C[i+1][j]+=C[i][j], C[i+1][j+1]+=C[i][j];

    scanf("%d%d%d", &N, &M, &L);
