#include <bits/stdc++.h>
using namespace std;
int main(){
  cout << fixed << setprecision(5);
  int N, M, L;
  cin >> N >> M >> L;
  vector<int> P(N), T(N), V(N);
  for (int i = 0; i < N; i++){
    cin >> P[i] >> T[i] >> V[i];
  }
  vector<vector<vector<double>>> dp(N, vector<vector<double>>(M + 1, vector<double>(M + 1, 0)));
  for (int i = 0; i < N; i++){
    dp[i][0][0] = 1;
    double p = (double) P[i] / 100;
    for (int j = 0; j < M; j++){
      for (int k = 0; k <= j; k++){
        dp[i][j + 1][k] += dp[i][j][k] * (1 - p);
        dp[i][j + 1][k + 1] += dp[i][j][k] * p;
      }
    }
  }
  for (int i = 0; i < N; i++){
    double ans = 0;
    for (int j = 0; j <= M; j++){
      double t = (double) L / V[i] + j * T[i];
      double tmp = dp[i][M][j];
      for (int k = 0; k < N; k++){
        if (k != i){
          double sum = 0;
          for (int l = 0; l <= M; l++){
            if ((double) L / V[k] + l * T[k] <= t){
              sum += dp[k][M][l];
            }
          }
          tmp *= 1 - sum;
        }
      }
      ans += tmp;
    }
    cout << ans << endl;
  }
}

