struct runner{
	int t,v;
	double p;
};

const int INF=1000000000;

vector<runner> vr;
double mem[51][51][101];
// kxe
double calcPer(int n,int k,int idx){
	if(!EQ(mem[n][k][idx],INF))
		return mem[n][k][idx];
	double p = vr[idx].p;
	if(k==0)
		return 1.0;
    return mem[n][k][idx]=(((double)n/k)*calcPer(n-1,k-1,idx)*p);
}
int main(){
	int n,m,L;
	for(int i = 0; i < 51; i++)
		for(int j = 0; j < 51; j++)
			for(int k = 0; k < 101; k++)
				mem[i][j][k]=INF;
	cin>>n>>m>>L;
	for(int i = 0; i < n; i++){
		runner r;
		int p;
		cin>>p>>r.t>>r.v;
		r.p=(double)p/100;
		vr.push_back(r);
	}
	// l
	for(int i = 0; i < n; i++){
		double sumPer=0.0;
		if(vr[i].v!=0){
			// xe
			for(int j = 0; j <= m; j++){
				// xexem
				double cper=calcPer(m,j,i)*pow((double)(1-vr[i].p),m-j);
                double selfTime=((double)L/vr[i].v)+vr[i].t*j;
				// G
				for(int k = 0; k < n; k++){
					// gAx0i[
					if(i==k||EQ(vr[k].v,0.0))
						continue;
					double enemPer=1.0;
					// xeA`FbN
					for(int l = 0; l <= m; l++){
                        // GBvZ
                        double enemyTime=((double)L/vr[k].v)+vr[k].t*l;
						// GBBI
						if(!EQ(enemyTime,selfTime)&&enemyTime>selfTime)
                            break;
                        // xem
                        double p=calcPer(m,l,k)*pow((double)(1-vr[k].p),m-l);
                        // xeoA}CiX
						enemPer-=p;
					}
					// cmAGxBm
					cper*=enemPer;
				}
				// cperm
				sumPer+=cper;
			}
		}
		printf("%.7f\n",sumPer);
	}
	return 0;
}

