using namespace std;
#define eps (1e-8)

int N,M;
double L;
double P[100],T[100],V[100];
double a[100][100],b[100][100];

double mem[100][100];
double ncr(int n,int r){
  if(mem[n][r]!=-1.0)return mem[n][r];
  r=min(r,n-r);
  double res=1.0;
  for(int i=0;i<r;i++){
    res=res*(double)(n-i);
    res=res/(double)(i+1);
  }
  return mem[n][r]=res;
}

double solve(int x){
  double res=0;
  for(int i=0;i<=M;i++){
    double p=a[x][i];
    double d=b[x][i];
    for(int j=0;j<N;j++){
      if(x==j)continue;
      double sum=0;
      for(int k=0;k<=M;k++){
        if(b[j][k]<d+eps)continue;
        sum+=a[j][k];
      }
      p*=sum;
    }
    res+=p;
  }
  return res;
}

int main(){
