		for (int i = 1; i <= m; i++) {
			for (int j = 0; j <= m; j++) {
				dp[i][j] = dp[i - 1][j] * (100.0 - p[h][0]) / 100.0;
				dp[i][j + 1] = dp[i - 1][j] * p[h][0] / 100.0;
			}
