#include<bits/stdc++.h>
#define EPS (1e-7)
#define T 6005
#define N 105
#define M 55
using namespace std;
int n,m,l,p[N],t[N],v[N];
double dp[N][2][T],sum[N][T];

int main(){
  cin>>n>>m>>l;
  for(int i=0;i<n;i++)
    cin>>p[i]>>t[i]>>v[i];
  for(int i=0;i<n;i++){
    dp[i][0][0]=1;
    for(int j=0;j<m;j++){
      for(int k=0;k<T;k++){
	if(!dp[i][j%2][k])continue;
	dp[i][(j+1)%2][k]+=dp[i][j%2][k]*(100.0-p[i])/100.0;
	dp[i][(j+1)%2][k+t[i]]+=dp[i][j%2][k]*p[i]/100.0;
      }
      for(int k=0;k<T;k++)dp[i][j%2][k]=0;
    }
  }
  for(int i=0;i<n;i++)
    for(int j=T-2;j>=0;j--)
      sum[i][j]=sum[i][j+1]+dp[i][m%2][j];
  for(int i=0;i<n;i++){
    double ans=0;
    if(!v[i])continue;
    for(int j=0;j<T;j++){
      if(!dp[i][m%2][j])continue;
      double x=dp[i][m%2][j];
      double s=j+1.0*l/v[i];
      for(int k=0;k<n;k++){
	if(i==k||!v[k])continue;
	int idx=max(0,(int)(s-1.0*l/v[k]+EPS)+1);
	if(-1<s-1.0*l/v[k]+EPS&&s-1.0*l/v[k]+EPS<0)idx--;
	if(T<=idx){
	  x=0;
	  break;
	}
	x*=sum[k][idx];
      }
      ans+=x;
    }
    printf("%.8f\n",ans);
  }
  return 0;
}

