    for(int j=T-2;j>=0;j--)
      sum[i][j]=sum[i][j+1]+dp[i][m%2][j];
  for(int i=0;i<n;i++){
    double ans=0;
    if(v[i])
      for(int j=0;j<T;j++){
	if(!dp[i][m%2][j])continue;
	double x=dp[i][m%2][j];
	double s=j+1.0*l/v[i];
 	for(int k=0;k<n;k++){
	  if(i==k||!v[k])continue;
	  int idx=max(0,(int)(s-1.0*l/v[k]+EPS)+1);
	  x*=sum[k][idx];
	}
	ans+=x;
      }
    printf("%.8f\n",ans);
  }
  return 0;
}

