const int MAXN = 105;
const int MAXM = 55;


int N, M, L;
int P[MAXN], T[MAXN], V[MAXN];
long long C[MAXM][MAXM];
void init() {
    for(int i = 1; i < MAXM; i++) {
        C[i][0] = 1;
        for(int j = 1; j <= i; j++)
            C[i][j] = C[i][j - 1] * (i - j + 1) / j;
    }
}

double ans[MAXN];
void calc(int cur_id) {
    if(V[cur_id] == 0) {
        ans[cur_id] = 0;
        return ;
    }
    double res = 0;
    for(int k = 0; k <= M; k++) {
        double ts = 1.0 * L / V[cur_id] + T[cur_id] * k;
        double pk = pow(1.0 * P[cur_id] / 100, k) * pow(1 - 1.0 * P[cur_id] / 100, M - k) * C[M][k];
        double res1 = 1;
        for(int i = 0; i < N; i++) if(i != cur_id) {
            if(V[i] == 0) continue;
            double ti = 1.0 * L / V[i];
            if(T[i] == 0) {
                res1 *= (ts - ti) <= 0;
                continue;
            }
            int k = (int)((ts - ti) / T[i] + 1.00000001);
            if(k < 0) continue;
            //k++;
            double pi = 0;
            for(int j = k; j <= M; j++) {
                pi += pow(1.0 * P[i] / 100, j) * pow(1 - 1.0 * P[i] / 100, M - j) * C[M][j];
            }
            res1 *= pi;
        }
        res += res1 * pk;
    }

    ans[cur_id] = res;
}
int main() {
    //freopen("in.txt", "r", stdin);
    init();
    while(scanf("%d%d%d", &N, &M, &L) != EOF) {
        for(int i = 0; i < N; i++)
            scanf("%d%d%d", &P[i], &T[i], &V[i]);
        for(int i = 0; i < N; i++) {
            calc(i);
        }
        for(int i = 0; i < N; i++)
            printf("%.10f\n", ans[i]);
    }
    return 0;
}

