			gao(p/100., dp[i], sum[i]);
//			debug(dp[i], 0, m+2);
//			debug(sum[i], 0, m+2); 
		}
		for (int i = 0; i < n; ++i) {
			double ans = 0;
			for (int j = 0; j <= m; ++j) {
				double p = dp[i][j];
				for (int r,k = 0; k < n; ++k) {
					if (i == k) continue;
					r = binsearch(i, j, k);
					p *= sum[k][r];
				}
				ans += p;
			}
			printf("%.10f\n", ans);
		}
	}
}

