				if(t[k] == 0){
					if(time >= base_time[k]) cul *= 0;
					continue;
				}

				int rest = (time - base_time[k]) / t[k];
				while(rest * t[k] + base_time[k] <= time) rest++;
				//cout << "rest " << rest << endl;
				if(rest > m){
					cul *= 0;
					continue;
				}
				cul *= 1 - c[k][rest];
				//show(cul)
				//show(1 - c[k][rest])
			}
			if(isnan(cul)) continue;
			//show(cul)
			//show(ans)
			ans += cul;
			//show(ans)
		}
		cout << fixed << setprecision(10)<<ans << endl;
	}
}

