#define eps 5e-6

using namespace std;

struct point
{
    double time,possible;
    int num;
};

int n,m,l,i,j,k,last,g[100][100],ii;
double a,b,c,p,q,ans[200],f[200],r;
point s[10000];

void insert(double time,double possible,int num)
{
    s[k].time=time;
    s[k].possible=possible;
    s[k].num=num;
    k++;
}

bool cmp(point a,point b)
{
    return a.time>b.time;
}

double pow(double a,int x)
{
    double ans=1.0,p=a;
    while(x)
    {
        if(x&1)ans=ans*p;
        x=x>>1;
        p=p*p;
    }
    return ans;
}

int main()
{
    for(i=0;i<60;i++)
    {
        g[i][0]=1;
        g[i][i]=1;
        for(j=1;j<i;j++)
            g[i][j]=g[i-1][j]+g[i-1][j-1];
    }
    scanf("%d%d%d",&n,&m,&l);
    k=0;
    for(i=0;i<n;i++)
    {
        scanf("%lf%lf%lf",&a,&b,&c);
        if(fabs(a-0.0)<eps)
        {
            insert(l*1.0/c,1.0,i);
            continue;
        }
        else if(fabs(a-100.0)<eps)
        {
            insert(l*1.0/c+b*m,1.0,i);
            continue;
        }
        p=a/100.0;
        q=1.0-p;
        a=l*1.0/c;
        r=1.0;
        for(j=0;j<=m;j++)
        {
            insert(a+j*b,g[m][j]*pow(p,j)*pow(q,m-j),i);
        }
    }
    memset(f,0,sizeof(f));
    sort(s,s+k,cmp);
    j=0;
    memset(ans,0,sizeof(ans));
    for(i=0;i<k;i++)
    {
        while(j<i && s[j].time-eps>s[i].time)
        {
            f[s[j].num]+=s[j].possible;
            j++;
        }
        p=s[i].possible;
        for(ii=0;ii<n;ii++)
        {
            if(ii==s[i].num)continue;
            p=p*f[ii];
        }
        ans[s[i].num]+=p;
    }
    p=0;
    for(i=0;i<n;i++)
        printf("%.8f\n",ans[i]);
    return 0;
}

