void Pascals(double m[N][N]){
	m[0][0] = 1;
	range(i,1,N){
		rep(j,i + 1){
			if(j == 0) m[i][j] = m[i - 1][j];
			else if(j == i) m[i][j] = m[i - 1][j - 1];
			else m[i][j] = (m[i - 1][j] + m[i - 1][j - 1]);
		}
	}
}

int main(){
	int n, m, l;
	cin >> n >> m >> l;

	vector<double> p(n), t(n), v(n);
	rep(i,n){
		cin >> p[i] >> t[i] >> v[i];
		p[i] /= 100;
	}

	double pas[N][N];
	Pascals(pas);
	double c[100][100] = {{0}};
	rep(i,n){
		double rest_p = 1;
		double run_p = 1;
		rep(j,m) run_p *= (1 - p[i]);
		rep(j,m + 1){
			c[i][j + 1] = c[i][j] + rest_p * run_p * pas[m][j];
			//show(c[i][j + 1])
			//cout << rest_p << ' ' << run_p << endl;
			rest_p *= p[i];
			run_p /= (1 - p[i]);
		}
	}


	vector<int> base_time(n);
	rep(i,n) base_time[i] = l / v[i];
	rep(i,n){
		double ans = 0;
		rep(j,m + 1){
			double cul = c[i][j + 1] - c[i][j];
			//cout << "saisyo " <<  cul << endl;
			double time = base_time[i] + j * t[i];
			//show(time)
			rep(k,n){
				if(i == k) continue;
				if(t[k] == 0){
					if(time >= base_time[k]) cul *= 0;
					continue;
				}

				int rest = ceil((time - base_time[k]) / t[k]);
				if(rest * t[k] + base_time[k] <= time) rest++;
				//cout << "rest " << rest << endl;
				if(rest == 0) cul *= 1;
				else if(rest > m) cul *= 0;
				else cul *= 1 - c[k][rest];
				//show(1 - c[k][rest])
			}
			//show(cul)
			ans += cul;
		}
		cout << fixed << setprecision(10)<<ans << endl;
	}
}

