using namespace std;

const int MAXN = 105;
const double EPS = 1e-12;

int n, m, l;
int P[MAXN], T[MAXN], V[MAXN];
double f[MAXN][55][55];
double sumf[MAXN][55];

void gao() {
	memset(f, 0, sizeof(f));
	for (int i = 0; i < n; ++i) {
		f[i][0][0] = 1;
		for (int j = 1; j <= m; ++j) {
			f[i][j][0] = f[i][j-1][0] * (1-1.0*P[i]/100);
				//printf("%d %d %d: %lf\n",i,j,0,f[i][j][0]);
			for (int k = 1; k <= j; ++k) {
				f[i][j][k] = f[i][j-1][k]*(1-1.0*P[i]/100) + f[i][j-1][k-1]*1.0*P[i]/100;
				//printf("%d %d %d: %lf\n",i,j,k,f[i][j][k]);
			}
		}
	}
	memset(sumf, 0, sizeof(sumf));
	for (int i = 0; i < n; ++i) {
		for (int j = m; j >= 0; --j) {
			sumf[i][j] = sumf[i][j+1] + f[i][m][j];
		//	printf("%lf ",sumf[i][j]);
		}
	}
}

void solve() {
	for (int i = 0; i < n; ++i) {
		double ans = 0;
		if (V[i] == 0) {
			printf("0.00000000\n");
			continue;
		}
		for (int k = 0; k <= m; ++k) {
			double Ti = l*1.0 / V[i] + k * T[i];
			//printf("Ti: %lf\t",Ti);
			double z = 1;
			for (int j = 0; j < n; ++j)
				if (j != i) {
					double tmp;
					if (T[j] == 0) {
						if (1LL*l*V[i] <= l*V[j] + 1LL*k*T[i]*V[i]*V[j]) z=0;
						tmp = -10;
					}
					else tmp = (Ti - l*1.0/V[j]) / T[j];
					int kj = (int)ceil(tmp-EPS); 
					if (fabs(tmp - kj) < EPS) ++kj;
					if (kj < 0) kj = 0;
					z *= sumf[j][kj];
					//printf("j: %d %d %lf\n",j,kj,sumf[j][kj]);
				}
			ans += z*f[i][m][k];
		}
		printf("%.8lf\n", ans);
	}
}

int main() {
	scanf("%d%d%d", &n, &m, &l);
	for (int i = 0; i < n; ++i) {
		scanf("%d%d%d", P+i, T+i, V+i);
	}
	gao();
	solve();
	return 0;
}

